import 'dart:convert';
import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;
import 'package:kakao_map_plugin/kakao_map_plugin.dart';
import 'package:geolocator/geolocator.dart';

class KakaoMapPharmacyScreen extends StatefulWidget {
  const KakaoMapPharmacyScreen({super.key});

  @override
  State<KakaoMapPharmacyScreen> createState() => _KakaoMapPharmacyScreenState();
}

class _KakaoMapPharmacyScreenState extends State<KakaoMapPharmacyScreen> {
  KakaoMapController? mapController;
  List<Marker> markers = [];
  LatLng? center;

  @override
  void initState() {
    super.initState();
    _initializeLocation();
  }

  Future<void> _initializeLocation() async {
    debugPrint("üìç ÏúÑÏπò Í∂åÌïú ÌôïÏù∏ Ï§ë...");
    final position = await _determinePosition();
    setState(() {
      center = LatLng(position.latitude, position.longitude);
    });
    await _searchNearby("ÏïΩÍµ≠");
  }

  Future<Position> _determinePosition() async {
    bool serviceEnabled = await Geolocator.isLocationServiceEnabled();
    if (!serviceEnabled) {
      throw Exception('‚ùó ÏúÑÏπò ÏÑúÎπÑÏä§ Í∫ºÏ†∏ ÏûàÏùå');
    }

    LocationPermission permission = await Geolocator.checkPermission();
    if (permission == LocationPermission.denied) {
      permission = await Geolocator.requestPermission();
    }

    if (permission == LocationPermission.deniedForever) {
      throw Exception('‚ùó ÏúÑÏπò Í∂åÌïú ÏòÅÍµ¨ Í±∞Î∂ÄÎê®');
    }

    return await Geolocator.getCurrentPosition();
  }

  Future<void> _searchNearby(String keyword) async {
    if (center == null) return;

    const String restApiKey = "4e3fad26fbcec489f06457486e212255";
    const String restApiKey2 = "015412fbc48c3a4e31b1926b6adb667e";
    final url = Uri.parse(
      'https://dapi.kakao.com/v2/local/search/keyword.json'
      '?query=$keyword&x=${center!.longitude}&y=${center!.latitude}&radius=2000',
    );

    final response = await http.get(
      url,
      headers: {
        "Authorization": "KakaoAK $restApiKey2",
        "KA": "sdk/1.0 app/medibot os/ios-15 lang/ko device/iPhone13,3",
      },
    );

    if (response.statusCode == 200) {
      final data = jsonDecode(response.body);
      final documents = data['documents'];

      print("üìç Í≤ÄÏÉâÎêú Ïû•ÏÜå Ïàò: ${documents.length}");

      setState(() {
        markers =
            documents.map<Marker>((doc) {
              return Marker(
                markerId: UniqueKey().toString(),
                latLng: LatLng(double.parse(doc['y']), double.parse(doc['x'])),
                infoWindowContent: doc['place_name'],
                markerImageSrc:
                    "data:image/jpeg;base64,",
                width: 40,
                height: 40,
              );
            }).toList();
      });
    } else {
      debugPrint("‚ùå Ïπ¥Ïπ¥Ïò§ API Ïã§Ìå®: ${response.body}");
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: const Text("Ï£ºÎ≥Ä ÏïΩÍµ≠", style: TextStyle(color: Colors.black)),
        centerTitle: true,
        backgroundColor: Colors.white,
        elevation: 0,
        iconTheme: const IconThemeData(color: Colors.black),
      ),
      body:
          center == null
              ? const Center(child: CupertinoActivityIndicator())
              : Stack(
                children: [
                  KakaoMap(
                    center: center!,
                    onMapCreated: (controller) {
                      mapController = controller;
                      controller.setLevel(3);
                    },
                    markers: markers,
                  ),
                  Positioned(
                    top: 20,
                    left: 20,
                    right: 20,
                    child: Container(
                      padding: const EdgeInsets.symmetric(
                        vertical: 10,
                        horizontal: 16,
                      ),
                      decoration: BoxDecoration(
                        color: Colors.white.withOpacity(0.95),
                        borderRadius: BorderRadius.circular(12),
                        boxShadow: [
                          BoxShadow(
                            color: Colors.black12,
                            blurRadius: 8,
                            offset: Offset(0, 3),
                          ),
                        ],
                      ),
                      child: const Text(
                        "ÌòÑÏû¨ ÏúÑÏπòÎ•º Í∏∞Ï§ÄÏúºÎ°ú Ï£ºÎ≥Ä ÏïΩÍµ≠ÏùÑ ÌëúÏãúÌï©ÎãàÎã§.",
                        style: TextStyle(fontSize: 15),
                      ),
                    ),
                  ),
                ],
              ),
      floatingActionButton: FloatingActionButton.extended(
        onPressed: () => _searchNearby("Î≥ëÏõê"),
        label: const Text("Î≥ëÏõê Í≤ÄÏÉâ", style: TextStyle(color: Colors.white)),
        icon: const Icon(Icons.local_hospital, color: Colors.white),
        backgroundColor: Colors.indigoAccent,
      ),
    );
  }
}
